import React, { useState } from 'react';
import './App.css';
import Header from './components/Header.js';
import TableComponent from './components/TableComponent.js';

function App() {
  const [taskCircleId, setTaskCircleId] = useState('');
  const [loading, setLoading] = useState(false);

  // 接收从 Header 组件传递的 taskCircleId 和 loading 状态
  const handleTaskCircleChange = (id, isLoading) => {
    setTaskCircleId(id);
    setLoading(isLoading);
  };

  return (
    <div className="App">
      <Header onTaskCircleChange={handleTaskCircleChange} />
      <div className="main-content">
        <div className="left-column">
          <TableComponent taskCircleId={taskCircleId} loading={loading} />
        </div>
      </div>
    </div>
  );
}

export default App;
