import React, { useState } from 'react';
// With these lines (adjust extensions based on your actual file types):
import Header from './components/Header.js';
import TableComponent from './components/TableComponent.js';
import StatisticsDashboard from './components/StatisticsDashboard.jsx';
import './App.css';

function App() {
  const [selectedPhaseId, setSelectedPhaseId] = useState(null);
  const [taskCircleId, setTaskCircleId] = useState('');
  const [loading, setLoading] = useState(false);

  // 接收从 Header 组件传递的 taskCircleId 和 loading 状态
  const handleTaskCircleChange = (id, isLoading) => {
    setTaskCircleId(id);
    setLoading(isLoading);
  };

  return (
    <div className="App">
      <Header onPhaseChange={setSelectedPhaseId} />
      {selectedPhaseId && <StatisticsDashboard selectedPhaseId={selectedPhaseId} />}
      <TableComponent />
      <div className="main-content">
        <div className="left-column">
          <TableComponent taskCircleId={taskCircleId} loading={loading} />
        </div>
      </div>
    </div>
  );
}

export default App;
