import React, { useState, useEffect } from 'react';
import dictConfig from '../config/dictConfig.js';
import { searchTasks } from '../services/taskService.js';

const SearchPanel = ({ onClose, onTaskSelect }) => {
  const [searchCriteria, setSearchCriteria] = useState({
    taskOrStep: '',
    isComplete: '',
    isLate: '',
    responsibility: ''
  });
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);

  // ÊâßË°åÊêúÁ¥¢
  const handleSearch = async () => {
    setLoading(true);
    setHasSearched(true);
    try {
      const results = await searchTasks(searchCriteria);
      setSearchResults(results);
    } catch (error) {
      console.error('ÊêúÁ¥¢Â§±Ë¥•:', error);
      alert('ÊêúÁ¥¢Â§±Ë¥•ÔºåËØ∑Á®çÂêéÂÜçËØï');
    } finally {
      setLoading(false);
    }
  };

  // Ê∏ÖÁ©∫ÊêúÁ¥¢Êù°‰ª∂
  const handleClear = () => {
    setSearchCriteria({
      taskOrStep: '',
      isComplete: '',
      isLate: '',
      responsibility: ''
    });
    setSearchResults([]);
    setHasSearched(false);
  };

  // ÈÄâÊã©‰ªªÂä°
  const handleTaskClick = (task) => {
    if (onTaskSelect) {
      onTaskSelect(task);
    }
    // ‰∏çÂÜçËá™Âä®ÂÖ≥Èó≠ÊêúÁ¥¢Èù¢ÊùøÔºå‰øùÊåÅÊêúÁ¥¢ÁªìÊûúÂèØËßÅ
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      right: 0,
      width: '20%',
      height: '100vh',
      backgroundColor: 'white',
      boxShadow: '-2px 0 8px rgba(0, 0, 0, 0.1)',
      zIndex: 9999,
      display: 'flex',
      flexDirection: 'column'
    }}>
      {/* Ê†áÈ¢òÊ†è */}
      <div style={{
        padding: '16px 20px',
        borderBottom: '1px solid #e0e0e0',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        backgroundColor: '#fafafa'
      }}>
        <h3 style={{ margin: 0, color: '#1890ff', fontSize: '16px' }}>‰ªªÂä°Ê£ÄÁ¥¢</h3>
        <button 
          onClick={onClose}
          style={{
            background: 'none',
            border: 'none',
            fontSize: '20px',
            cursor: 'pointer',
            color: '#999',
            padding: '0',
            width: '24px',
            height: '24px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}
        >
          √ó
        </button>
      </div>

      {/* ÊêúÁ¥¢Êù°‰ª∂ */}
      <div style={{
        padding: '20px',
        borderBottom: '1px solid #e0e0e0'
      }}>
        <div style={{ marginBottom: '16px' }}>
          <label style={{ display: 'block', marginBottom: '6px', fontSize: '14px', fontWeight: 'bold' }}>
            ‰ªªÂä°/Ê≠•È™§
          </label>
          <input
            type="text"
            value={searchCriteria.taskOrStep}
            onChange={(e) => setSearchCriteria({ ...searchCriteria, taskOrStep: e.target.value })}
            placeholder="ËæìÂÖ•‰ªªÂä°ÂêçÁß∞ÊàñÊ≠•È™§ÂÜÖÂÆπ"
            style={{
              width: '100%',
              padding: '8px 12px',
              border: '1px solid #d9d9d9',
              borderRadius: '4px',
              fontSize: '14px'
            }}
          />
        </div>

        <div style={{ marginBottom: '16px' }}>
          <label style={{ display: 'block', marginBottom: '6px', fontSize: '14px', fontWeight: 'bold' }}>
            ÊòØÂê¶ÂÆåÊàê
          </label>
          <select
            value={searchCriteria.isComplete}
            onChange={(e) => setSearchCriteria({ ...searchCriteria, isComplete: e.target.value })}
            style={{
              width: '100%',
              padding: '8px 12px',
              border: '1px solid #d9d9d9',
              borderRadius: '4px',
              fontSize: '14px'
            }}
          >
            <option value="">ÂÖ®ÈÉ®</option>
            <option value="ÊòØ">ÊòØ</option>
            <option value="Âê¶">Âê¶</option>
          </select>
        </div>

        <div style={{ marginBottom: '16px' }}>
          <label style={{ display: 'block', marginBottom: '6px', fontSize: '14px', fontWeight: 'bold' }}>
            ÊòØÂê¶Ë∂ÖÊúü
          </label>
          <select
            value={searchCriteria.isLate}
            onChange={(e) => setSearchCriteria({ ...searchCriteria, isLate: e.target.value })}
            style={{
              width: '100%',
              padding: '8px 12px',
              border: '1px solid #d9d9d9',
              borderRadius: '4px',
              fontSize: '14px'
            }}
          >
            <option value="">ÂÖ®ÈÉ®</option>
            <option value="ÊòØ">ÊòØ</option>
            <option value="Âê¶">Âê¶</option>
          </select>
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '6px', fontSize: '14px', fontWeight: 'bold' }}>
            Ë¥£‰ªª‰∫∫
          </label>
          <select
            value={searchCriteria.responsibility}
            onChange={(e) => setSearchCriteria({ ...searchCriteria, responsibility: e.target.value })}
            style={{
              width: '100%',
              padding: '8px 12px',
              border: '1px solid #d9d9d9',
              borderRadius: '4px',
              fontSize: '14px'
            }}
          >
            <option value="">ÂÖ®ÈÉ®</option>
            {dictConfig.responsiblePerson.map(person => (
              <option key={person} value={person}>{person}</option>
            ))}
          </select>
        </div>

        {/* Êìç‰ΩúÊåâÈíÆ */}
        <div style={{ display: 'flex', gap: '8px' }}>
          <button
            onClick={handleSearch}
            disabled={loading}
            style={{
              flex: 1,
              padding: '10px',
              backgroundColor: loading ? '#f5f5f5' : '#1890ff',
              color: loading ? '#999' : 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: loading ? 'not-allowed' : 'pointer',
              fontSize: '14px'
            }}
          >
            {loading ? 'ÊêúÁ¥¢‰∏≠...' : 'ÊêúÁ¥¢'}
          </button>
          <button
            onClick={handleClear}
            style={{
              flex: 1,
              padding: '10px',
              backgroundColor: '#f5f5f5',
              color: '#666',
              border: '1px solid #d9d9d9',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '14px'
            }}
          >
            Ê∏ÖÁ©∫
          </button>
        </div>
      </div>

      {/* ÊêúÁ¥¢ÁªìÊûú */}
      <div style={{
        flex: 1,
        overflow: 'auto',
        padding: '20px'
      }}>
