import React, { useState, useEffect } from 'react';
import { Card, CardContent, Grid, Typography, Box } from '@mui/material';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, RadialBarChart, RadialBar, Legend, ReferenceLine, Arc }
 from 'recharts';
import { getCurrentTaskCircleStats } from '../services/taskCircleService.js';

const StatisticsDashboard = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  // 刷新数据函数
  const fetchStats = async () => {
    try {
      setLoading(true);
      const data = await getCurrentTaskCircleStats();
      setStats(data);
    } catch (error) {
      console.error('获取统计数据失败:', error);
    } finally {
      setLoading(false);
    }
  };

  // 初始加载和触发刷新时获取数据
  useEffect(() => {
    fetchStats();
  }, [refreshTrigger]);

  // 提供给父组件的刷新方法
  const refreshData = () => {
    setRefreshTrigger(prev => prev + 1);
  };

  // 颜色配置（不超过3种主色）
  const COLORS = {
    primary: '#3f51b5',
    secondary: '#f50057',
    tertiary: '#4caf50'
  };

  if (loading || !stats) {
    return <Box display="flex" justifyContent="center" p={3}>加载中...</Box>;
  }

  // 任务统计数据
  const taskData = [
    { name: '总任务', value: stats.total_task },
    { name: '已完成', value: stats.complete_task },
    { name: '已超期', value: stats.late_task }
  ];

  // 步骤统计数据
  const stepData = [
    { name: '已完成', value: stats.complete_step },
    { name: '进行中', value: stats.going_step },
    { name: '未开始', value: stats.not_start_step },
    { name: '已超期', value: stats.late_step }
  ];

  return (
    <Box mb={3}>
      <Typography variant="h6" gutterBottom>项目统计</Typography>
      <Grid container spacing={3} alignItems="stretch">
        {/* 任务统计 - 柱状图 */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="subtitle1" gutterBottom>任务统计</Typography>
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={taskData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="value" fill={COLORS.primary} />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </Grid>

        {/* 步骤统计 - 饼图 */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="subtitle1" gutterBottom>步骤状态分布</Typography>
              <ResponsiveContainer width="100%" height={250}>
                <PieChart>
                  <Pie
                    data={stepData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    outerRadius={80}
                    fill={COLORS.primary}
                    dataKey="value"
                    label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                  >
                    {stepData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={
                        index === 0 ? COLORS.primary :
                        index === 1 ? COLORS.secondary :
                        index === 2 ? COLORS.tertiary : '#ff9800'
                      } />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </Grid>

        {/* 完成率 - 仪表盘 */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="subtitle1" gutterBottom>完成率</Typography>
              <ResponsiveContainer width="100%" height={250}>
                // Replace completion percentage gauge:
                <ResponsiveContainer width="100%" height={200}>
                  <RadialBarChart innerRadius={60} outerRadius={90} data={[{ name: 'Completion', value: stats.complete_percent }]} startAngle={90} endAngle={-270}>
                    <RadialBar background clockWise={true} dataKey="value" fill={COMPLETE_COLOR} />
                    <Legend iconType="circle" layout="vertical" verticalAlign="middle" align="right" />
                    <Tooltip formatter={(value) => [`${value}%`, 'Completion Rate']} />
                  </RadialBarChart>
                </ResponsiveContainer>
                
                // Replace late percentage gauge:
                <ResponsiveContainer width="100%" height={200}>
                  <RadialBarChart innerRadius={60} outerRadius={90} data={[{ name: 'Late', value: stats.late_percent }]} startAngle={90} endAngle={-270}>
                    <RadialBar background clockWise={true} dataKey="value" fill={LATE_COLOR} />
                    <Legend iconType="circle" layout="vertical" verticalAlign="middle" align="right" />
                    <Tooltip formatter={(value) => [`${value}%`, 'Late Rate']} />
                  </RadialBarChart>
                </ResponsiveContainer>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </Grid>

        {/* 超期率 - 仪表盘 */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="subtitle1" gutterBottom>超期率</Typography>
              <ResponsiveContainer width="100%" height={250}>
                <GaugeChart id="gauge-chart-late" data={[{ value: stats.late_percent }]}>
                  <Gauge
                    dataKey="value"
                    strokeWidth={20}
                    color={COLORS.secondary}
                    startAngle={90}
                    endAngle={-270}
                    arcWidth={20}
                  />
                  <ReferenceLine stroke="#ccc" strokeWidth={2} />
                  <Tooltip formatter={(value) => [`${value}%`, '超期率']} />
                </GaugeChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default StatisticsDashboard;
