import React, { useState } from 'react';
import './App.css';
import Header from './components/Header.js';
import TableComponent from './components/TableComponent.js'; // 导入表格组件

function App() {
  const [selectedYear, setSelectedYear] = useState('');
  const [selectedMonth, setSelectedMonth] = useState('');
  const [selectedPhase, setSelectedPhase] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleYearMonthPhaseChange = (year, month, phase) => {
    setIsLoading(true);
    setSelectedYear(year);
    setSelectedMonth(month);
    setSelectedPhase(phase);
    // 这里可以添加延迟，模拟加载过程
    setTimeout(() => {
      setIsLoading(false);
    }, 500);
  };

  return (
    <div className="App">
      <Header 
        onYearMonthPhaseChange={handleYearMonthPhaseChange} 
        loading={isLoading} 
      />
      <div className="main-content">
        <div className="left-column">
          <TableComponent 
            year={selectedYear} 
            month={selectedMonth} 
            phase={selectedPhase} 
            isLoading={isLoading} 
          />
        </div>
      </div>
    </div>
  );
}

export default App;
