import axios from 'axios';

const API_URL = 'http://localhost:3001/api';

// 创建或更新任务
export const saveTask = (taskData) => {
  return axios.post(`${API_URL}/tasks`, taskData);
};

// 获取所有任务
export const getTasks = () => {
  return axios.get(`${API_URL}/tasks`);
};

