import React, { useRef, useEffect, useState } from 'react';
import './TableComponent.css';

const TableComponent = () => {
  const [showAddRow, setShowAddRow] = useState(false);
  const [newTaskName, setNewTaskName] = useState('');

  // 空数据数组 - 将从API获取数据
  const [tableData, setTableData] = useState([]);

  // 移除列宽调整相关代码

  const handleAddTask = () => {
    if (newTaskName.trim()) {
      // 添加新任务到表格数据
      setTableData([
        ...tableData,
        { 
          id: Date.now(), // 临时ID，实际应用中应从API获取
          task_name: newTaskName 
          // 其他字段将在后续添加
        }
      ]);
      
      // 重置表单
      setNewTaskName('');
      setShowAddRow(false);
    }
  };

  return (
    <div className="table-container">
      <table className="resizable-table">
        <thead>
          <tr>
            <th>任务名称</th>
            <th>任务步骤</th>
            <th>开始日期</th>
            <th>截至日期</th>
            <th>责任人</th>
            <th>当前状态</th>
            <th>是否完成</th>
            <th>是否超期</th>
            <th>优先级</th>
            <th>备注</th>
          </tr>
        </thead>
        <tbody>
          {/* 新任务的可编辑行 */}
          {showAddRow && (
            <tr className="new-task-row">
              <td>
                <div className="new-task-input-container">
                  <input
                    type="text"
                    value={newTaskName}
                    onChange={(e) => setNewTaskName(e.target.value)}
                    placeholder="输入任务名称"
                    autoFocus
                  />
                  <div className="new-task-buttons">
                    <button className="save-task-btn" onClick={handleAddTask}>保存</button>
                    <button className="cancel-task-btn" onClick={() => setShowAddRow(false)}>取消</button>
                  </div>
                </div>
              </td>
              {/* 其他列的空单元格 */}
              {[...Array(9)].map((_, i) => (
                <td key={`empty-${i}`}></td>
              ))}
            </tr>
          )}
          
          {/* 现有任务 */}
          {tableData.map(row => (
            <tr key={row.id}>
              <td>{row.task_name}</td>
              <td>{row.task_step || ''}</td>
              <td>{row.startdate || ''}</td>
              <td>{row.enddate || ''}</td>
              <td>{row.responsibility || ''}</td>
              <td>{row.taskstate || ''}</td>
              <td>{row.iscomplete ? '是' : '否'}</td>
              <td>{row.islate ? '是' : '否'}</td>
              <td>{row.priority || ''}</td>
              <td>{row.remark || ''}</td>
            </tr>
          ))}
          
          {/* 带添加按钮的持久行 */}
          <tr className="add-task-row">
            <td>
              <button 
                className="add-task-btn" 
                onClick={() => setShowAddRow(true)}
              >
                +
              </button>
            </td>
            {/* 其他列的空单元格 */}
            {[...Array(9)].map((_, i) => (
              <td key={`add-empty-${i}`}></td>
            ))}
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default TableComponent;
