import React, { useRef, useEffect, useState } from 'react';
import './TableComponent.css';

const TableComponent = () => {
  const tableRef = useRef(null);
  const [showAddRow, setShowAddRow] = useState(false);
  const [newTaskName, setNewTaskName] = useState('');
  const [newTaskDetails, setNewTaskDetails] = useState({});
  const [expandedRowId, setExpandedRowId] = useState(null);

  // 空数据数组 - 将从API获取数据
  const [tableData, setTableData] = useState([]);

  // 列宽调整功能保持不变
  useEffect(() => {
    if (!tableRef.current) return;

    const table = tableRef.current;
    const cols = table.querySelectorAll('th');
    let isResizing = false;
    let currentCol = null;
    let startX = 0;
    let startWidth = 0;

    const handleMouseDown = (e) => {
      if (e.target.classList.contains('resizer')) {
        isResizing = true;
        currentCol = e.target.parentElement;
        startX = e.pageX;
        startWidth = currentCol.offsetWidth;
        document.body.style.cursor = 'col-resize';
      }
    };

    const handleMouseMove = (e) => {
      if (!isResizing) return;
      const width = startWidth + (e.pageX - startX);
      if (width > 50) {
        currentCol.style.width = `${width}px`;
      }
    };

    const handleMouseUp = () => {
      isResizing = false;
      document.body.style.cursor = '';
    };

    table.addEventListener('mousedown', handleMouseDown);
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      table.removeEventListener('mousedown', handleMouseDown);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, []);

  const handleAddTask = () => {
    if (newTaskName.trim()) {
      // 添加新任务到表格数据
      const newTask = {
        id: Date.now(), // 临时ID，实际应用中应从API获取
        task_name: newTaskName,
        hasDetails: false
        // 其他字段将在后续添加
      };

      setTableData([
        ...tableData,
        newTask
      ]);

      // 重置表单
      setNewTaskName('');
      setShowAddRow(false);
    }
  };

  const handleAddDetails = (taskId) => {
    setExpandedRowId(taskId);
    setNewTaskDetails({});
  };

  const handleSaveDetails = (taskId) => {
    // 找到对应的任务并更新
    const updatedData = tableData.map(task => {
      if (task.id === taskId) {
        return {
          ...task,
          ...newTaskDetails,
          hasDetails: true
        };
      }
      return task;
    });

    setTableData(updatedData);
    setExpandedRowId(null);
  };

  return (
    <div className="table-container">
      <table ref={tableRef} className="resizable-table">
        <thead>
          <tr>
            <th>任务名称 <div className="resizer"></div></th>
            <th>任务步骤 <div className="resizer"></div></th>
            <th>开始日期 <div className="resizer"></div></th>
            <th>截至日期 <div className="resizer"></div></th>
            <th>责任人 <div className="resizer"></div></th>
            <th>当前状态 <div className="resizer"></div></th>
            <th>是否完成 <div className="resizer"></div></th>
            <th>是否超期 <div className="resizer"></div></th>
            <th>优先级 <div className="resizer"></div></th>
            <th>备注 <div className="resizer"></div></th>
          </tr>
        </thead>
        <tbody>
          {/* 新任务的可编辑行 */}
          {showAddRow && (
            <tr className="new-task-row">
              <td>
                <div className="new-task-input-container">
                  <input
                    type="text"
                    value={newTaskName}
                    onChange={(e) => setNewTaskName(e.target.value)}
                    placeholder="输入任务名称"
                    autoFocus
                  />
                  <div className="new-task-buttons">
                    <button className="save-task-btn" onClick={handleAddTask}>保存</button>
                    <button className="cancel-task-btn" onClick={() => setShowAddRow(false)}>取消</button>
                  </div>
                </div>
              </td>
              {/* 其他列的空单元格 */}
              {[...Array(9)].map((_, i) => (
                <td key={`empty-${i}`}></td>
              ))}
            </tr>
          )}

          {/* 现有任务 */}
          {tableData.map((row, index) => (
            <React.Fragment key={row.id}>
              <tr className="task-row">
                <td>{row.task_name}</td>
                <td>{row.task_step || ''}</td>
                <td>{row.startdate || ''}</td>
                <td>{row.enddate || ''}</td>
                <td>{row.responsibility || ''}</td>
                <td>{row.taskstate || ''}</td>
                <td>{row.iscomplete ? '是' : '否'}</td>
                <td>{row.islate ? '是' : '否'}</td>
                <td>{row.priority || ''}</td>
                <td>{row.remark || ''}</td>
              </tr>

              {/* 任务步骤的加号按钮行 */}
              <tr className="add-step-row">
                <td></td>
                <td>
                  <button
                    className="add-step-btn"
                    onClick={() => handleAddDetails(row.id)}
                  >
                    +
                  </button>
                </td>
                {/* 其他列的空单元格 */}
                {[...Array(8)].map((_, i) => (
                  <td key={`step-empty-${i}`}></td>
                ))}
              </tr>

              {/* 编辑详情行 */}
              {expandedRowId === row.id && (
                <tr className="edit-details-row">
                  <td colSpan={1}></td>
                  <td>
                    <input
                      type="text"
                      value={newTaskDetails.task_step || ''}
                      onChange={(e) => setNewTaskDetails({...newTaskDetails, task_step: e.target.value})}
                      placeholder="任务步骤"
                    />
                  </td>
                  <td>
                    <input
                      type="date"
                      value={newTaskDetails.startdate || ''}
                      onChange={(e) => setNewTaskDetails({...newTaskDetails, startdate: e.target.value})}
                    />
                  </td>
                  <td>
                    <input
                      type="date"
                      value={newTaskDetails.enddate || ''}
                      onChange={(e) => setNewTaskDetails({...newTaskDetails, enddate: e.target.value})}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={newTaskDetails.responsibility || ''}
                      onChange={(e) => setNewTaskDetails({...newTaskDetails, responsibility: e.target.value})}
                      placeholder="责任人"
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={newTaskDetails.taskstate || ''}
                      onChange={(e) => setNewTaskDetails({...newTaskDetails, taskstate: e.target.value})}
                      placeholder="当前状态"
                    />
                  </td>
                  <td>
                    <select
                      value={newTaskDetails.iscomplete ? '1' : '0'}
                      onChange={(e) => setNewTaskDetails({...newTaskDetails, iscomplete: e.target.value === '1'})}
                    >
                      <option value="0">否</option>
                      <option value="1">是</option>
                    </select>
                  </td>
                  <td>
                    <select
                      value={newTaskDetails.islate ? '1' : '0'}
                      onChange={(e) => setNewTaskDetails({...newTaskDetails, islate: e.target.value === '1'})}
                    >
                      <option value="0">否</option>
                      <option value="1">是</option>
                    </select>
                  </td>
                  <td>
                    <input
                      type="text"
                      value={newTaskDetails.priority || ''}
                      onChange={(e) => setNewTaskDetails({...newTaskDetails, priority: e.target.value})}
                      placeholder="优先级"
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={newTaskDetails.remark || ''}
                      onChange={(e) => setNewTaskDetails({...newTaskDetails, remark: e.target.value})}
                      placeholder="备注"
                    />
                    <div className="details-buttons">
                      <button className="save-details-btn" onClick={() => handleSaveDetails(row.id)}>保存</button>
                      <button className="cancel-details-btn" onClick={() => setExpandedRowId(null)}>取消</button>
                    </div>
                  </td>
                </tr>
              )}
            </React.Fragment>
          ))}

          {/* 带添加按钮的持久行 */}
          <tr className="add-task-row">
            <td>
              <button
                className="add-task-btn"
                onClick={() => setShowAddRow(true)}
              >
                +
              </button>
            </td>
            {/* 其他列的空单元格 */}
            {[...Array(9)].map((_, i) => (
              <td key={`add-empty-${i}`}></td>
            ))}
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default TableComponent;
