import React from 'react';
import './Header.css';
import { useState, useEffect, useRef } from 'react';
import {
  getYears,
  getMonthsByYear,
  getPhasesByYearAndMonth,
  checkYearMonthPhaseExists,
  createTaskCircle
} from '../services/taskCircleService.js';

export default function Header() {
  // 状态管理
  const [years, setYears] = useState([]);
  const [months, setMonths] = useState([]);
  const [phases, setPhases] = useState([]);
  const [selectedYear, setSelectedYear] = useState('');
  const [selectedMonth, setSelectedMonth] = useState('');
  const [selectedPhase, setSelectedPhase] = useState('');
  const [yearInput, setYearInput] = useState('');
  const [monthInput, setMonthInput] = useState('');
  const [phaseInput, setPhaseInput] = useState('');
  const [isAddPlanEnabled, setIsAddPlanEnabled] = useState(false);
  const [showYearDropdown, setShowYearDropdown] = useState(false);
  const [showMonthDropdown, setShowMonthDropdown] = useState(false);
  const [showPhaseDropdown, setShowPhaseDropdown] = useState(false);

  // 引用
  const yearRef = useRef(null);
  const monthRef = useRef(null);
  const phaseRef = useRef(null);

  // 组件加载时获取年份
  useEffect(() => {
    const fetchYears = async () => {
      const data = await getYears();
      setYears(data);
    };
    fetchYears();
  }, []);

  // 监听年份变化，更新月份
  useEffect(() => {
    if (selectedYear) {
      const fetchMonths = async () => {
        const data = await getMonthsByYear(selectedYear);
        setMonths(data);
        setSelectedMonth('');
        setPhases([]);
        setSelectedPhase('');
      };
      fetchMonths();
    } else {
      setMonths([]);
      setPhases([]);
    }
  }, [selectedYear]);

  // 监听月份变化，更新阶段
  useEffect(() => {
    if (selectedYear && selectedMonth) {
      const fetchPhases = async () => {
        const data = await getPhasesByYearAndMonth(selectedYear, selectedMonth);
        setPhases(data);
        setSelectedPhase('');
      };
      fetchPhases();
    } else if (selectedYear) {
      setPhases([]);
    }
  }, [selectedYear, selectedMonth]);

  // 监听输入变化，检查是否存在匹配数据
  useEffect(() => {
    if (yearInput && monthInput && phaseInput) {
      const checkExists = async () => {
        try {
          console.log('检查数据是否存在:', yearInput, monthInput, phaseInput);
          const exists = await checkYearMonthPhaseExists(
            yearInput,
            monthInput,
            phaseInput
          );
          console.log('数据存在结果:', exists);
          // 只有当数据不存在时才启用按钮
          setIsAddPlanEnabled(!exists);
          console.log('按钮状态:', !exists);
        } catch (error) {
          console.error('检查年月阶段失败:', error);
          // 发生错误时禁用按钮
          setIsAddPlanEnabled(false);
        }
      };
      checkExists();
    } else {
      setIsAddPlanEnabled(false);
      console.log('输入不完整，禁用按钮');
    }
  }, [yearInput, monthInput, phaseInput]);

  // 处理增加计划按钮点击
  const handleAddPlan = async () => {
    if (isAddPlanEnabled) {
      try {
        await createTaskCircle(yearInput, monthInput, phaseInput);
        // 成功后可以刷新年份列表或提示用户
        alert('计划已成功添加！');
        // 重置输入
        setYearInput('');
        setMonthInput('');
        setPhaseInput('');
        setSelectedYear('');
        setSelectedMonth('');
        setSelectedPhase('');
        setMonths([]);
        setPhases([]);
        // 刷新年份列表
        const data = await getYears();
        setYears(data);
      } catch (error) {
        console.error('添加计划失败:', error);
        alert('添加计划失败，请稍后再试。');
      }
    }
  };

  // 点击空白处关闭下拉菜单
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        yearRef.current && !yearRef.current.contains(event.target)
      ) {
        setShowYearDropdown(false);
      }
      if (
        monthRef.current && !monthRef.current.contains(event.target)
      ) {
        setShowMonthDropdown(false);
      }
      if (
        phaseRef.current && !phaseRef.current.contains(event.target)
      ) {
        setShowPhaseDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // 处理年份选择
  const handleYearSelect = (year) => {
    setSelectedYear(year);
    setYearInput(year);
    setShowYearDropdown(false);
  };

  // 处理月份选择
  const handleMonthSelect = (month) => {
    setSelectedMonth(month);
    setMonthInput(month);
    setShowMonthDropdown(false);
  };

  // 处理阶段选择
  const handlePhaseSelect = (phase) => {
    setSelectedPhase(phase);
    setPhaseInput(phase);
    setShowPhaseDropdown(false);
  };

  return (
    <div className="header-container">
      <div className="title-wrapper">
        <h2 className="title">
          <div className="dropdown-container" ref={yearRef}>
            <input
              type="text"
              placeholder="年"
              value={yearInput}
              onChange={(e) => {
                setYearInput(e.target.value);
                setSelectedYear('');
              }}
              onClick={() => setShowYearDropdown(!showYearDropdown)}
            />
            年
            {showYearDropdown && (
              <div className="dropdown">
                {years.map((year) => (
                  <div
                    key={year}
                    className="dropdown-item"
                    onClick={() => handleYearSelect(year)}
                  >
                    {year}
                  </div>
                ))}
              </div>
            )}
          </div>
          <div className="dropdown-container" ref={monthRef}>
            <input
              type="text"
              placeholder="月"
              value={monthInput}
              onChange={(e) => {
                setMonthInput(e.target.value);
                setSelectedMonth('');
              }}
              onClick={() => setShowMonthDropdown(!showMonthDropdown)}
            />
            月
            {showMonthDropdown && (
              <div className="dropdown">
                {months.map((month) => (
                  <div
                    key={month}
                    className="dropdown-item"
                    onClick={() => handleMonthSelect(month)}
                  >
                    {month}
                  </div>
                ))}
              </div>
            )}
          </div>
          <div className="text-container">第</div>
          <div className="dropdown-container" ref={phaseRef}>
            <input
              type="text"
              placeholder="阶段"
              value={phaseInput}
              onChange={(e) => {
                setPhaseInput(e.target.value);
                setSelectedPhase('');
              }}
              onClick={() => setShowPhaseDropdown(!showPhaseDropdown)}
            />
            阶段项目交付及产品研发计划
            {showPhaseDropdown && (
              <div className="dropdown">
                {phases.map((phase) => (
                  <div
                    key={phase}
                    className="dropdown-item"
                    onClick={() => handlePhaseSelect(phase)}
                  >
                    {phase}
                  </div>
                ))}
              </div>
            )}
          </div>
        </h2>
      </div>
      <button
        className="add-plan"
        disabled={!isAddPlanEnabled}
        onClick={handleAddPlan}
      >
        增加计划
      </button>
    </div>
  );
}
