import axios from 'axios';

// Define BASE_URL
export const BASE_URL = 'http://localhost:3001/api';

// Get all tasks
export const getTasks = () => {
  return axios.get(`${BASE_URL}/tasks`);
};

// Save a task (create or update)
export const saveTask = (taskData) => {
  if (taskData.id) {
    return axios.put(`${BASE_URL}/tasks/${taskData.id}`, taskData);
  } else {
    return axios.post(`${BASE_URL}/tasks`, taskData);
  }
};

// Update a specific task
export const updateTask = (id, taskData) => {
  return axios.put(`${BASE_URL}/tasks/${id}`, taskData);
};

// Create a new task
export const createTask = (taskData) => {
  return axios.post(`${BASE_URL}/tasks`, taskData);
};

// Delete a task
export const deleteTask = (id) => {
  return axios.delete(`${BASE_URL}/tasks/${id}`);
};

// Update a step
export const updateStep = (id, stepData) => {
  return axios.put(`${BASE_URL}/dashboard/${id}`, stepData);
};

// Create a new step
export const createStep = (stepData) => {
  return axios.post(`${BASE_URL}/dashboard`, stepData);
};

// Delete a step
export const deleteStep = (id) => {
  return axios.delete(`${BASE_URL}/dashboard/${id}`);
};
