import React, { useRef, useEffect, useState } from 'react';
import './TableComponent.css';

const TableComponent = () => {
  const tableRef = useRef(null);
  const [data, setData] = useState([]);
  const [editingRow, setEditingRow] = useState(null);
  const [newRowData, setNewRowData] = useState({
    task_name: '',
    task_step: '',
    startdate: '',
    enddate: '',
    responsibility: '',
    taskstate: '',
    iscomplete: false,
    islate: false,
    priority: '',
    remark: ''
  });

  // 列宽调整功能
  useEffect(() => {
    if (!tableRef.current) return;

    const table = tableRef.current;
    const cols = table.querySelectorAll('th');
    let isResizing = false;
    let currentCol = null;
    let startX = 0;
    let startWidth = 0;

    const handleMouseDown = (e) => {
      if (e.target.classList.contains('resizer')) {
        isResizing = true;
        currentCol = e.target.parentElement;
        startX = e.pageX;
        startWidth = currentCol.offsetWidth;
        document.body.style.cursor = 'col-resize';
      }
    };

    const handleMouseMove = (e) => {
      if (!isResizing) return;
      const width = startWidth + (e.pageX - startX);
      if (width > 50) {
        currentCol.style.width = `${width}px`;
      }
    };

    const handleMouseUp = () => {
      isResizing = false;
      document.body.style.cursor = '';
    };

    table.addEventListener('mousedown', handleMouseDown);
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      table.removeEventListener('mousedown', handleMouseDown);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, []);

  // 添加新行
  const addNewRow = () => {
    setData([...data, { ...newRowData, id: Date.now() }]);
    // 重置新行数据
    setNewRowData({
      task_name: '',
      task_step: '',
      startdate: '',
      enddate: '',
      responsibility: '',
      taskstate: '',
      iscomplete: false,
      islate: false,
      priority: '',
      remark: ''
    });
  };

  // 处理输入变化
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setNewRowData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  return (
    <div className="table-container">
      <table ref={tableRef} className="resizable-table">
        <thead>
          <tr>
            <th>任务名称 <div className="resizer"></div></th>
            <th>任务步骤 <div className="resizer"></div></th>
            <th>开始日期 <div className="resizer"></div></th>
            <th>截至日期 <div className="resizer"></div></th>
            <th>责任人 <div className="resizer"></div></th>
            <th>当前状态 <div className="resizer"></div></th>
            <th>是否完成 <div className="resizer"></div></th>
            <th>是否超期 <div className="resizer"></div></th>
            <th>优先级 <div className="resizer"></div></th>
            <th>备注 <div className="resizer"></div></th>
          </tr>
        </thead>
        <tbody>
          {/* 数据行 */}
          {data.map(row => (
            <tr key={row.id} className={editingRow === row.id ? 'editing' : ''}>
              <td>{row.task_name}</td>
              <td>{row.task_step}</td>
              <td>{row.startdate}</td>
              <td>{row.enddate}</td>
              <td>{row.responsibility}</td>
              <td>{row.taskstate}</td>
              <td>{row.iscomplete ? '是' : '否'}</td>
              <td>{row.islate ? '是' : '否'}</td>
              <td>{row.priority}</td>
              <td>{row.remark}</td>
            </tr>
          ))}

          {/* 可编辑的新行 */}
          <tr className="editable-row">
            <td>
              <button className="add-row-button" onClick={addNewRow}>+</button>
            </td>
            <td><input type="text" name="task_step" value={newRowData.task_step} onChange={handleInputChange} /></td>
            <td><input type="date" name="startdate" value={newRowData.startdate} onChange={handleInputChange} /></td>
            <td><input type="date" name="enddate" value={newRowData.enddate} onChange={handleInputChange} /></td>
            <td><input type="text" name="responsibility" value={newRowData.responsibility} onChange={handleInputChange} /></td>
            <td><input type="text" name="taskstate" value={newRowData.taskstate} onChange={handleInputChange} /></td>
            <td><input type="checkbox" name="iscomplete" checked={newRowData.iscomplete} onChange={handleInputChange} /></td>
            <td><input type="checkbox" name="islate" checked={newRowData.islate} onChange={handleInputChange} /></td>
            <td><input type="text" name="priority" value={newRowData.priority} onChange={handleInputChange} /></td>
            <td><input type="text" name="remark" value={newRowData.remark} onChange={handleInputChange} /></td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default TableComponent;
