import React, { useRef, useEffect, useState } from 'react';
import './TableComponent.css';

const TableComponent = () => {
  const tableRef = useRef(null);
  const [showAddRow, setShowAddRow] = useState(false);
  const [newTaskName, setNewTaskName] = useState('');
  const [expandedRowId, setExpandedRowId] = useState(null);
  const [expandedRowData, setExpandedRowData] = useState({});

  // 空数据数组 - 将从API获取数据
  const [tableData, setTableData] = useState([]);

  // 列宽调整功能保持不变
  useEffect(() => {
    if (!tableRef.current) return;

    const table = tableRef.current;
    const cols = table.querySelectorAll('th');
    let isResizing = false;
    let currentCol = null;
    let startX = 0;
    let startWidth = 0;

    const handleMouseDown = (e) => {
      if (e.target.classList.contains('resizer')) {
        isResizing = true;
        currentCol = e.target.parentElement;
        startX = e.pageX;
        startWidth = currentCol.offsetWidth;
        document.body.style.cursor = 'col-resize';
      }
    };

    const handleMouseMove = (e) => {
      if (!isResizing) return;
      const width = startWidth + (e.pageX - startX);
      if (width > 50) {
        currentCol.style.width = `${width}px`;
      }
    };

    const handleMouseUp = () => {
      isResizing = false;
      document.body.style.cursor = '';
    };

    table.addEventListener('mousedown', handleMouseDown);
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      table.removeEventListener('mousedown', handleMouseDown);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, []);

  const handleAddTask = () => {
    if (newTaskName.trim()) {
      // 添加新任务到表格数据
      const newTask = {
        id: Date.now(), // 临时ID，实际应用中应从API获取
        task_name: newTaskName
        // 其他字段将在后续添加
      };

      setTableData([
        ...tableData,
        newTask
      ]);

      // 重置表单
      setNewTaskName('');
      setShowAddRow(false);
    }
  };

  const handleExpandRow = (id) => {
    setExpandedRowId(id);
    // 初始化扩展行数据
    const task = tableData.find(row => row.id === id);
    setExpandedRowData({
      ...task,
      id: Date.now() + 1 // 确保ID唯一
    });
  };

  const handleSaveExpandedRow = () => {
    // 在这里可以处理保存扩展行数据的逻辑
    // 例如，更新原始任务或添加新任务
    setExpandedRowId(null);
  };

  return (
    <div className="table-container">
      <table ref={tableRef} className="resizable-table">
        <thead>
          <tr>
            <th>任务名称 <div className="resizer"></div></th>
            <th>任务步骤 <div className="resizer"></div></th>
            <th>开始日期 <div className="resizer"></div></th>
            <th>截至日期 <div className="resizer"></div></th>
            <th>责任人 <div className="resizer"></div></th>
            <th>当前状态 <div className="resizer"></div></th>
            <th>是否完成 <div className="resizer"></div></th>
            <th>是否超期 <div className="resizer"></div></th>
            <th>优先级 <div className="resizer"></div></th>
            <th>备注 <div className="resizer"></div></th>
          </tr>
        </thead>
        <tbody>
          {/* 新任务的可编辑行 */}
          {showAddRow && (
            <tr className="new-task-row">
              <td>
                <div className="new-task-input-container">
                  <input
                    type="text"
                    value={newTaskName}
                    onChange={(e) => setNewTaskName(e.target.value)}
                    placeholder="输入任务名称"
                    autoFocus
                  />
                  <div className="new-task-buttons">
                    <button className="save-task-btn" onClick={handleAddTask}>保存</button>
                    <button className="cancel-task-btn" onClick={() => setShowAddRow(false)}>取消</button>
                  </div>
                </div>
              </td>
              {/* 其他列的空单元格 */}
              {[...Array(9)].map((_, i) => (
                <td key={`empty-${i}`}></td>
              ))}
            </tr>
          )}

          {/* 现有任务 */}
          {tableData.map(row => (
            <React.Fragment key={row.id}>
              <tr key={`task-${row.id}`}>
                <td>{row.task_name}</td>
                <td>{row.task_step || ''}</td>
                <td>{row.startdate || ''}</td>
                <td>{row.enddate || ''}</td>
                <td>{row.responsibility || ''}</td>
                <td>{row.taskstate || ''}</td>
                <td>{row.iscomplete ? '是' : '否'}</td>
                <td>{row.islate ? '是' : '否'}</td>
                <td>{row.priority || ''}</td>
                <td>{row.remark || ''}</td>
              </tr>

              {/* 任务步骤加号按钮行 */}
              <tr key={`add-step-${row.id}`} className="add-step-row">
                <td></td>
                <td>
                  <button 
                    className="add-step-btn"
                    onClick={() => handleExpandRow(row.id)}
                  >
                    +
                  </button>
                </td>
                {/* 其他列的空单元格 */}
                {[...Array(8)].map((_, i) => (
                  <td key={`add-step-empty-${i}`}></td>
                ))}
              </tr>

              {/* 展开的编辑行 */}
              {expandedRowId === row.id && (
                <tr key={`expanded-${row.id}`} className="expanded-row">
                  <td>{row.task_name} <span className="readonly-label">(不可编辑)</span></td>
                  <td>
                    <input
                      type="text"
                      value={expandedRowData.task_step || ''}
                      onChange={(e) => setExpandedRowData({...expandedRowData, task_step: e.target.value})}
                      placeholder="输入任务步骤"
                    />
                  </td>
                  <td>
                    <input
                      type="date"
                      value={expandedRowData.startdate || ''}
                      onChange={(e) => setExpandedRowData({...expandedRowData, startdate: e.target.value})}
                    />
                  </td>
                  <td>
                    <input
                      type="date"
                      value={expandedRowData.enddate || ''}
                      onChange={(e) => setExpandedRowData({...expandedRowData, enddate: e.target.value})}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={expandedRowData.responsibility || ''}
                      onChange={(e) => setExpandedRowData({...expandedRowData, responsibility: e.target.value})}
                      placeholder="输入责任人"
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={expandedRowData.taskstate || ''}
                      onChange={(e) => setExpandedRowData({...expandedRowData, taskstate: e.target.value})}
                      placeholder="输入当前状态"
                    />
                  </td>
                  <td>
                    <select
                      value={expandedRowData.iscomplete ? '1' : '0'}
                      onChange={(e) => setExpandedRowData({...expandedRowData, iscomplete: e.target.value === '1'})}
                    >
                      <option value="0">否</option>
                      <option value="1">是</option>
                    </select>
                  </td>
                  <td>
                    <select
                      value={expandedRowData.islate ? '1' : '0'}
                      onChange={(e) => setExpandedRowData({...expandedRowData, islate: e.target.value === '1'})}
                    >
                      <option value="0">否</option>
                      <option value="1">是</option>
                    </select>
                  </td>
                  <td>
                    <input
                      type="text"
                      value={expandedRowData.priority || ''}
                      onChange={(e) => setExpandedRowData({...expandedRowData, priority: e.target.value})}
                      placeholder="输入优先级"
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={expandedRowData.remark || ''}
                      onChange={(e) => setExpandedRowData({...expandedRowData, remark: e.target.value})}
                      placeholder="输入备注"
                    />
                  </td>
                </tr>
              )}
            </React.Fragment>
          ))}

          {/* 带添加按钮的持久行 */}
          <tr className="add-task-row">
            <td>
              <button 
                className="add-task-btn" 
                onClick={() => setShowAddRow(true)}
              >
                +
              </button>
            </td>
            {/* 其他列的空单元格 */}
            {[...Array(9)].map((_, i) => (
              <td key={`add-empty-${i}`}></td>
            ))}
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default TableComponent;
