import React, { useState, useEffect } from 'react';
import accessConfig from '../config/accessConfig.js';

const AccessConfigManager = ({ onClose }) => {
  const [config, setConfig] = useState({
    enableRedirectAccess: false,
    allowedReferrers: [],
    accessDeniedMessage: '访问被拒绝：请通过指定系统跳转访问本系统'
  });
  const [newReferrer, setNewReferrer] = useState('');

  useEffect(() => {
    // 加载当前配置
    setConfig({
      enableRedirectAccess: accessConfig.enableRedirectAccess,
      allowedReferrers: [...accessConfig.allowedReferrers],
      accessDeniedMessage: accessConfig.accessDeniedMessage
    });
  }, []);

  const handleSave = () => {
    // 更新配置
    accessConfig.updateConfig(config);
    alert('配置已保存！');
    if (onClose) onClose();
  };

  const addReferrer = () => {
    if (newReferrer.trim() && !config.allowedReferrers.includes(newReferrer.trim())) {
      setConfig({
        ...config,
        allowedReferrers: [...config.allowedReferrers, newReferrer.trim()]
      });
      setNewReferrer('');
    }
  };

  const removeReferrer = (index) => {
    const newReferrers = config.allowedReferrers.filter((_, i) => i !== index);
    setConfig({
      ...config,
      allowedReferrers: newReferrers
    });
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      zIndex: 10000,
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center'
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '8px',
        width: '600px',
        maxHeight: '80%',
        overflow: 'auto',
        padding: '0'
      }}>
        {/* 标题栏 */}
        <div style={{
          padding: '20px',
          borderBottom: '1px solid #e0e0e0',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <h2 style={{ margin: 0, color: '#fa8c16' }}>访问控制配置</h2>
          <button 
            onClick={onClose}
            style={{
              background: 'none',
              border: 'none',
              fontSize: '24px',
              cursor: 'pointer',
              color: '#999'
            }}
          >
            ×
          </button>
        </div>

        {/* 配置内容 */}
        <div style={{ padding: '20px' }}>
          {/* 开启跳转访问控制 */}
          <div style={{ marginBottom: '24px' }}>
            <label style={{
              display: 'flex',
              alignItems: 'center',
              fontSize: '16px',
              fontWeight: 'bold',
              marginBottom: '8px'
            }}>
              <input
                type="checkbox"
                checked={config.enableRedirectAccess}
                onChange={(e) => setConfig({
                  ...config,
                  enableRedirectAccess: e.target.checked
                })}
                style={{ marginRight: '8px' }}
              />
              开启跳转访问控制
            </label>
            <p style={{ color: '#666', fontSize: '14px', margin: '4px 0' }}>
              开启后，只允许从指定系统跳转访问，直接访问将被拒绝
            </p>
          </div>

          {/* 允许的来源系统 */}
          <div style={{ marginBottom: '24px' }}>
            <h3 style={{ fontSize: '16px', marginBottom: '12px' }}>允许的来源系统地址</h3>

            {/* 添加新地址 */}
            <div style={{ display: 'flex', marginBottom: '12px' }}>
              <input
                type="text"
                value={newReferrer}
                onChange={(e) => setNewReferrer(e.target.value)}
                placeholder="输入系统地址，如：https://example.com"
                style={{
                  flex: 1,
                  padding: '8px 12px',
                  border: '1px solid #d9d9d9',
                  borderRadius: '4px',
                  marginRight: '8px'
                }}
                onKeyPress={(e) => {
                  if (e.key === 'Enter') {
                    addReferrer();
                  }
                }}
              />
              <button
                onClick={addReferrer}
                style={{
                  padding: '8px 16px',
                  backgroundColor: '#1890ff',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                添加
              </button>
            </div>

            {/* 地址列表 */}
            <div style={{
              border: '1px solid #e0e0e0',
              borderRadius: '4px',
              minHeight: '100px',
              padding: '8px'
            }}>
              {config.allowedReferrers.length === 0 ? (
                <div style={{ color: '#999', textAlign: 'center', padding: '20px' }}>
                  暂无配置的来源系统
                </div>
              ) : (
                config.allowedReferrers.map((referrer, index) => (
                  <div key={index} style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '8px 12px',
                    backgroundColor: '#f5f5f5',
                    borderRadius: '4px',
                    marginBottom: '4px'
                  }}>
                    <span>{referrer}</span>
                    <button
                      onClick={() => removeReferrer(index)}
                      style={{
                        background: 'none',
                        border: 'none',
                        color: '#ff4d4f',
                        cursor: 'pointer',
                        fontSize: '16px'
                      }}
                    >
                      ×
                    </button>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* 访问拒绝提示信息 */}
          <div style={{ marginBottom: '24px' }}>
            <h3 style={{ fontSize: '16px', marginBottom: '8px' }}>访问拒绝提示信息</h3>
            <textarea
              value={config.accessDeniedMessage}
              onChange={(e) => setConfig({
                ...config,
                accessDeniedMessage: e.target.value
              })}
              style={{
                width: '100%',
                height: '80px',
                padding: '8px 12px',
                border: '1px solid #d9d9d9',
                borderRadius: '4px',
                resize: 'vertical',
                fontFamily: 'inherit'
              }}
              placeholder="输入访问被拒绝时显示的提示信息"
            />
          </div>

          {/* 操作按钮 */}
          <div style={{
            display: 'flex',
            justifyContent: 'flex-end',
            gap: '12px',
            borderTop: '1px solid #e0e0e0',
            paddingTop: '16px'
          }}>
            <button
              onClick={onClose}
              style={{
                padding: '8px 16px',
                backgroundColor: '#f5f5f5',
                color: '#666',
                border: '1px solid #d9d9d9',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              取消
            </button>
            <button
              onClick={handleSave}
              style={{
                padding: '8px 16px',
                backgroundColor: '#52c41a',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              保存配置
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AccessConfigManager;
