import React, { useRef, useEffect, useState } from 'react';
import './TableComponent.css';
import {
  getTasksByYearMonthPhase,
  addTask,
  addStep
} from '../services/taskService.js';

const TableComponent = ({ year, month, phase, isLoading }) => {
  const tableRef = useRef(null);
  const [showAddTaskRow, setShowAddTaskRow] = useState(false);
  const [showAddStepRow, setShowAddStepRow] = useState(null); // 存储当前要添加步骤的任务ID
  const [newTaskName, setNewTaskName] = useState('');
  const [newStepData, setNewStepData] = useState({
    task_step: '',
    startdate: '',
    enddate: '',
    responsibility: '',
    taskstate: '',
    iscomplete: false,
    islate: false,
    priority: '',
    remark: ''
  });
  // 添加编辑状态管理
  const [editingCell, setEditingCell] = useState(null); // 存储当前编辑的单元格信息 {taskId, stepId, field}
  const [editValue, setEditValue] = useState(''); // 存储当前编辑的值

  // 数据数组
  const [tableData, setTableData] = useState([]);

  // 当年份、月份或阶段变化时，获取相关的任务和步骤
  useEffect(() => {
    if (year && month && phase) {
      const fetchData = async () => {
        try {
          const data = await getTasksByYearMonthPhase(year, month, phase);
          setTableData(data);
        } catch (error) {
          console.error('获取任务和步骤数据失败:', error);
          setTableData([]);
        }
      };
      fetchData();
    } else {
      setTableData([]);
    }
  }, [year, month, phase]);

  // 列宽调整功能保持不变
  useEffect(() => {
    if (!tableRef.current) return;

    const table = tableRef.current;
    const cols = table.querySelectorAll('th');
    let isResizing = false;
    let currentCol = null;
    let startX = 0;
    let startWidth = 0;

    const handleMouseDown = (e) => {
      if (e.target.classList.contains('resizer')) {
        isResizing = true;
        currentCol = e.target.parentElement;
        startX = e.pageX;
        startWidth = currentCol.offsetWidth;
        document.body.style.cursor = 'col-resize';
      }
    };

    const handleMouseMove = (e) => {
      if (!isResizing) return;
      const width = startWidth + (e.pageX - startX);
      if (width > 50) {
        currentCol.style.width = `${width}px`;
      }
    };

    const handleMouseUp = () => {
      isResizing = false;
      document.body.style.cursor = '';
    };

    table.addEventListener('mousedown', handleMouseDown);
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      table.removeEventListener('mousedown', handleMouseDown);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, []);

  const handleAddTask = async () => {
    if (newTaskName.trim() && year && month && phase) {
      try {
        // 调用API添加任务
        const newTask = await addTask(newTaskName, year, month, phase);
        // 更新表格数据
        setTableData([
          ...tableData,
          newTask
        ]);

        // 重置表单
        setNewTaskName('');
        setShowAddTaskRow(false);
      } catch (error) {
        console.error('添加任务失败:', error);
        alert('添加任务失败，请稍后再试。');
      }
    }
  };

  const handleAddStep = (taskId) => {
    setShowAddStepRow(taskId);
    // 重置步骤表单
    setNewStepData({
      task_step: '',
      startdate: '',
      enddate: '',
      responsibility: '',
      taskstate: '',
      iscomplete: false,
      islate: false,
      priority: '',
      remark: ''
    });
  };

  const handleSaveStep = async (taskId) => {
    if (newStepData.task_step.trim() && year && month && phase) {
      try {
        // 调用API添加步骤
        const newStep = await addStep(taskId, newStepData, year, month, phase);
        // 更新表格数据
        const updatedData = tableData.map(task => {
          if (task.id === taskId) {
            // 添加新步骤
            return {
              ...task,
              steps: [...task.steps, newStep]
            };
          }
          return task;
        });

        setTableData(updatedData);
        setShowAddStepRow(null);
      } catch (error) {
        console.error('添加步骤失败:', error);
        alert('添加步骤失败，请稍后再试。');
      }
    }
  };

  // 处理双击单元格事件
  const handleCellDoubleClick = (taskId, stepId, field, value) => {
    if (isLoading) return; // 加载过程中不可编辑
    setEditingCell({ taskId, stepId, field });
    setEditValue(value);
  };

  // 处理保存编辑
  const handleSaveEdit = () => {
    if (!editingCell || isLoading) return; // 加载过程中不可编辑

    const { taskId, stepId, field } = editingCell;

    // 更新数据
    const updatedData = tableData.map(task => {
      if (task.id === taskId) {
        // 如果是任务名称字段
        if (field === 'task_name') {
          return {
            ...task,
            task_name: editValue
          };
        }
        // 如果是步骤字段
        else if (stepId) {
          return {
            ...task,
            steps: task.steps.map(step => {
              if (step.id === stepId) {
                return {
                  ...step,
                  [field]: editValue
                };
              }
              return step;
            })
          };
        }
      }
      return task;
    });

    setTableData(updatedData);
    setEditingCell(null);
  };

  // 处理取消编辑
  const handleCancelEdit = () => {
    setEditingCell(null);
  };

  return (
    <div className="table-container">
      {isLoading && (
        <div className="loading-overlay">
          <div className="loading-spinner"></div>
        </div>
      )}
      <table ref={tableRef} className="resizable-table">
        <thead>
          <tr>
            <th>任务名称 <div className="resizer"></div></th>
            <th>任务步骤 <div className="resizer"></div></th>
            <th>开始日期 <div className="resizer"></div></th>
            <th>截至日期 <div className="resizer"></div></th>
            <th>责任人 <div className="resizer"></div></th>
            <th>当前状态 <div className="resizer"></div></th>
            <th>是否完成 <div className="resizer"></div></th>
            <th>是否超期 <div className="resizer"></div></th>
            <th>优先级 <div className="resizer"></div></th>
            <th>备注 <div className="resizer"></div></th>
          </tr>
        </thead>
        <tbody>
          {/* 新任务的可编辑行 */}
          {showAddTaskRow && !isLoading && (
            <tr className="new-task-row">
              <td>
                <div className="new-task-input-container">
                  <input
                    type="text"
                    value={newTaskName}
                    onChange={(e) => setNewTaskName(e.target.value)}
                    placeholder="输入任务名称"
                    autoFocus
                  />
                  <div className="new-task-buttons">
                    <button className="save-task-btn" onClick={handleAddTask}>保存</button>
                    <button className="cancel-task-btn" onClick={() => setShowAddTaskRow(false)}>取消</button>
                  </div>
                </div>
              </td>
              {/* 其他列的空单元格 */}
              {[...Array(9)].map((_, i) => (
                <td key={`empty-${i}`}></td>
              ))}
            </tr>
          )}

          {/* 添加任务按钮行 */}
          {!showAddTaskRow && !isLoading && (
            <tr className="add-task-row">
              <td colSpan="10">
                <button
                  className="add-task-btn"
                  onClick={() => setShowAddTaskRow(true)}
                >
                  增加任务
                </button>
              </td>
            </tr>
          )}

          {/* 现有任务和步骤 */}
          {tableData.map(task => (
            <React.Fragment key={task.id}>
              {/* 任务行 */}
              <tr className="task-row">
                <td
                  onDoubleClick={() => handleCellDoubleClick(task.id, null, 'task_name', task.task_name)}
                >
                  {editingCell && editingCell.taskId === task.id && editingCell.field === 'task_name' && !editingCell.stepId && !isLoading ? (
                    <div className="edit-cell-container">
                      <input
                        type="text"
                        value={editValue}
                        onChange={(e) => setEditValue(e.target.value)}
                        autoFocus
                      />
                      <div className="edit-buttons">
                        <button className="save-edit-btn" onClick={handleSaveEdit}>保存</button>
                        <button className="cancel-edit-btn" onClick={handleCancelEdit}>取消</button>
                      </div>
                    </div>
                  ) : (
                    task.task_name
                  )}
                </td>
                <td colSpan="9">
                  <button
                    className="add-step-btn"
                    onClick={() => handleAddStep(task.id)}
                    disabled={isLoading}
                  >
                    增加步骤
                  </button>
                </td> {/* 合并其他列 */}
              </tr>

              {/* 任务的步骤 */}
              {task.steps.map(step => (
                <tr key={step.id} className="step-row">
                  <td></td> {/* 任务名称列为空 */}
                  <td
                    onDoubleClick={() => handleCellDoubleClick(task.id, step.id, 'task_step', step.task_step)}
                  >
                    {editingCell && editingCell.taskId === task.id && editingCell.stepId === step.id && editingCell.field === 'task_step' && !isLoading ? (
                      <div className="edit-cell-container">
                        <input
                          type="text"
                          value={editValue}
                          onChange={(e) => setEditValue(e.target.value)}
                          autoFocus
                        />
                        <div className="edit-buttons">
                          <button className="save-edit-btn" onClick={handleSaveEdit}>保存</button>
                          <button className="cancel-edit-btn" onClick={handleCancelEdit}>取消</button>
                        </div>
                      </div>
                    ) : (
                      step.task_step
                    )}
                  </td>
                  <td
                    onDoubleClick={() => handleCellDoubleClick(task.id, step.id, 'startdate', step.startdate)}
                  >
                    {editingCell && editingCell.taskId === task.id && editingCell.stepId === step.id && editingCell.field === 'startdate' && !isLoading ? (
                      <div className="edit-cell-container">
                        <input
                          type="date"
                          value={editValue}
                          onChange={(e) => setEditValue(e.target.value)}
                          autoFocus
                        />
                        <div className="edit-buttons">
                          <button className="save-edit-btn" onClick={handleSaveEdit}>保存</button>
                          <button className="cancel-edit-btn" onClick={handleCancelEdit}>取消</button>
                        </div>
                      </div>
                    ) : (
                      step.startdate
                    )}
                  </td>
                  <td
                    onDoubleClick={() => handleCellDoubleClick(task.id, step.id, 'enddate', step.enddate)}
                  >
                    {editingCell && editingCell.taskId === task.id && editingCell.stepId === step.id && editingCell.field === 'enddate' && !isLoading ? (
                      <div className="edit-cell-container">
                        <input
                          type="date"
                          value={editValue}
                          onChange={(e) => setEditValue(e.target.value)}
                          autoFocus
                        />
                        <div className="edit-buttons">
                          <button className="save-edit-btn" onClick={handleSaveEdit}>保存</button>
                          <button className="cancel-edit-btn" onClick={handleCancelEdit}>取消</button>
                        </div>
                      </div>
                    ) : (
                      step.enddate
                    )}
                  </td>
                  <td
                    onDoubleClick={() => handleCellDoubleClick(task.id, step.id, 'responsibility', step.responsibility)}
                  >
                    {editingCell && editingCell.taskId === task.id && editingCell.stepId === step.id && editingCell.field === 'responsibility' && !isLoading ? (
                      <div className="edit-cell-container">
                        <input
                          type="text"
                          value={editValue}
                          onChange={(e) => setEditValue(e.target.value)}
                          autoFocus
                        />
                        <div className="edit-buttons">
                          <button className="save-edit-btn" onClick={handleSaveEdit}>保存</button>
                          <button className="cancel-edit-btn" onClick={handleCancelEdit}>取消</button>
                        </div>
                      </div>
                    ) : (
                      step.responsibility
                    )}
                  </td>
                  <td
                    onDoubleClick={() => handleCellDoubleClick(task.id, step.id, 'taskstate', step.taskstate)}
                  >
                    {editingCell && editingCell.taskId === task.id && editingCell.stepId === step.id && editingCell.field === 'taskstate' && !isLoading ? (
                      <div className="edit-cell-container">
                        <input
                          type="text"
                          value={editValue}
                          onChange={(e) => setEditValue(e.target.value)}
                          autoFocus
                        />
                        <div className="edit-buttons">
                          <button className="save-edit-btn" onClick={handleSaveEdit}>保存</button>
                          <button className="cancel-edit-btn" onClick={handleCancelEdit}>取消</button>
                        </div>
                      </div>
                    ) : (
                      step.taskstate
                    )}
                  </td>
                  <td
                    onDoubleClick={() => handleCellDoubleClick(task.id, step.id, 'iscomplete', step.iscomplete)}
                  >
                    {editingCell && editingCell.taskId === task.id && editingCell.stepId === step.id && editingCell.field === 'iscomplete' && !isLoading ? (
                      <div className="edit-cell-container">
                        <select
                          value={editValue ? 'true' : 'false'}
                          onChange={(e) => setEditValue(e.target.value === 'true')}
                          autoFocus
                        >
                          <option value="true">是</option>
                          <option value="false">否</option>
                        </select>
                        <div className="edit-buttons">
                          <button className="save-edit-btn" onClick={handleSaveEdit}>保存</button>
                          <button className="cancel-edit-btn" onClick={handleCancelEdit}>取消</button>
                        </div>
                      </div>
                    ) : (
                      step.iscomplete ? '是' : '否'
                    )}
                  </td>
                  <td
                    onDoubleClick={() => handleCellDoubleClick(task.id, step.id, 'islate', step.islate)}
                  >
                    {editingCell && editingCell.taskId === task.id && editingCell.stepId === step.id && editingCell.field === 'islate' && !isLoading ? (
                      <div className="edit-cell-container">
                        <select
                          value={editValue ? 'true' : 'false'}
                          onChange={(e) => setEditValue(e.target.value === 'true')}
                          autoFocus
                        >
                          <option value="true">是</option>
                          <option value="false">否</option>
                        </select>
                        <div className="edit-buttons">
                          <button className="save-edit-btn" onClick={handleSaveEdit}>保存</button>
                          <button className="cancel-edit-btn" onClick={handleCancelEdit}>取消</button>
                        </div>
                      </div>
                    ) : (
                      step.islate ? '是' : '否'
                    )}
                  </td>
                  <td
                    onDoubleClick={() => handleCellDoubleClick(task.id, step.id, 'priority', step.priority)}
                  >
                    {editingCell && editingCell.taskId === task.id && editingCell.stepId === step.id && editingCell.field === 'priority' && !isLoading ? (
                      <div className="edit-cell-container">
                        <input
                          type="text"
                          value={editValue}
                          onChange={(e) => setEditValue(e.target.value)}
                          autoFocus
                        />
                        <div className="edit-buttons">
                          <button className="save-edit-btn" onClick={handleSaveEdit}>保存</button>
                          <button className="cancel-edit-btn" onClick={handleCancelEdit}>取消</button>
                        </div>
                      </div>
                    ) : (
                      step.priority
                    )}
                  </td>
                  <td
                    onDoubleClick={() => handleCellDoubleClick(task.id, step.id, 'remark', step.remark)}
                  >
                    {editingCell && editingCell.taskId === task.id && editingCell.stepId === step.id && editingCell.field === 'remark' && !isLoading ? (
                      <div className="edit-cell-container">
                        <input
                          type="text"
                          value={editValue}
                          onChange={(e) => setEditValue(e.target.value)}
                          autoFocus
                        />
                        <div className="edit-buttons">
                          <button className="save-edit-btn" onClick={handleSaveEdit}>保存</button>
                          <button className="cancel-edit-btn" onClick={handleCancelEdit}>取消</button>
                        </div>
                      </div>
                    ) : (
                      step.remark
                    )}
                  </td>
                </tr>
              ))}

              {/* 添加步骤的可编辑行 */}
              {showAddStepRow === task.id && !isLoading && (
                <tr className="new-step-row">
                  <td></td> {/* 任务名称列为空 */}
                  <td>
                    <input
                      type="text"
                      value={newStepData.task_step}
                      onChange={(e) => setNewStepData({...newStepData, task_step: e.target.value})}
                      placeholder="输入步骤名称"
                      autoFocus
                    />
                  </td>
                  <td>
                    <input
                      type="date"
                      value={newStepData.startdate}
                      onChange={(e) => setNewStepData({...newStepData, startdate: e.target.value})}
                    />
                  </td>
                  <td>
                    <input
                      type="date"
                      value={newStepData.enddate}
                      onChange={(e) => setNewStepData({...newStepData, enddate: e.target.value})}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={newStepData.responsibility}
                      onChange={(e) => setNewStepData({...newStepData, responsibility: e.target.value})}
                      placeholder="输入责任人"
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={newStepData.taskstate}
                      onChange={(e) => setNewStepData({...newStepData, taskstate: e.target.value})}
                      placeholder="输入状态"
                    />
                  </td>
                  <td>
                    <select
                      value={newStepData.iscomplete ? 'true' : 'false'}
                      onChange={(e) => setNewStepData({...newStepData, iscomplete: e.target.value === 'true'})}
                    >
                      <option value="true">是</option>
                      <option value="false">否</option>
                    </select>
                  </td>
                  <td>
                    <select
                      value={newStepData.islate ? 'true' : 'false'}
                      onChange={(e) => setNewStepData({...newStepData, islate: e.target.value === 'true'})}
                    >
                      <option value="true">是</option>
                      <option value="false">否</option>
                    </select>
                  </td>
                  <td>
                    <input
                      type="text"
                      value={newStepData.priority}
                      onChange={(e) => setNewStepData({...newStepData, priority: e.target.value})}
                      placeholder="输入优先级"
                    />
                  </td>
                  <td>
                    <div className="new-step-buttons">
                      <button className="save-step-btn" onClick={() => handleSaveStep(task.id)}>保存</button>
                      <button className="cancel-step-btn" onClick={() => setShowAddStepRow(null)}>取消</button>
                    </div>
                  </td>
                </tr>
              )}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TableComponent;
