import React, { useState, useEffect } from 'react';
import './TableComponent.css';

// 假设的字段长度配置，可以根据实际数据库字段长度进行调整
const fieldLengths = {
  '任务名称': 20,
  '任务步骤': 30,
  '开始日期': 15,
  '截至日期': 15,
  '责任人': 10,
  '当前状态': 10,
  '是否完成': 8,
  '是否超期': 8,
  '优先级': 8,
  '备注': 40
};

// 根据字段长度计算列宽（这里只是一个简单的计算方式，可以根据实际需求调整）
const calculateColumnWidth = (fieldName) => {
  const baseWidth = 50;
  const length = fieldLengths[fieldName] || 10;
  return `${baseWidth + length * 5}px`;
};

function TableComponent() {
  const [columns, setColumns] = useState([
    '任务名称', '任务步骤', '开始日期', '截至日期', '责任人', '当前状态', '是否完成', '是否超期', '优先级', '备注'
  ]);
  const [columnWidths, setColumnWidths] = useState({});
  const [tableData, setTableData] = useState([]);
  const [newTaskName, setNewTaskName] = useState('');
  const [showNewTaskRow, setShowNewTaskRow] = useState(false);

  // 初始化列宽
  useEffect(() => {
    const widths = {}
    columns.forEach(column => {
      widths[column] = calculateColumnWidth(column);
    });
    setColumnWidths(widths);
  }, []);

  const handleAddTask = () => {
    if (newTaskName.trim()) {
      // 添加新任务到表格数据
      setTableData([
        ...tableData,
        { 
          id: Date.now(), // 临时ID，实际应用中应从API获取
          task_name: newTaskName 
          // 其他字段将在后续添加
        }
      ]);
      
      // 重置表单
      setNewTaskName('');
      setShowAddRow(false);
    }
  };

  return (
    <div className="table-container">
      <table className="resizable-table">
        <thead>
          <tr>
            {columns.map((column, index) => (
              <th key={index} style={{ width: columnWidths[column] }}>
                {column}
                <div className="resizer" onMouseDown={(e) => handleResizeStart(e, column)}></div>
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {/* 新增任务行 */}
          {showNewTaskRow && (
            <tr className="new-task-row">
              <td colSpan={columns.length}>
                <div className="new-task-input-container">
                  <input
                    type="text"
                    value={newTaskName}
                    onChange={(e) => setNewTaskName(e.target.value)}
                    placeholder="输入任务名称"
                  />
                  <div className="new-task-buttons">
                    <button className="save-task-btn" onClick={saveNewTask}>保存</button>
                    <button className="cancel-task-btn" onClick={() => setShowNewTaskRow(false)}>取消</button>
                  </div>
                </div>
              </td>
            </tr>
          )}

          {/* 加号按钮行 */}
          <tr className="add-task-row">
            <td>
              <button className="add-task-btn" onClick={() => setShowNewTaskRow(true)}>+</button>
            </td>
            {columns.slice(1).map((_, index) => (
              <td key={index}></td>
            ))}
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default TableComponent;
