import React, { useRef, useEffect, useState } from 'react';
import './TableComponent.css';

const TableComponent = () => {
  const tableRef = useRef(null);
  const [data, setData] = useState([]);
  const [editingRow, setEditingRow] = useState(null);
  const [newRowData, setNewRowData] = useState({
    task_name: '',
    task_step: '',
    startdate: '',
    enddate: '',
    responsibility: '',
    taskstate: '',
    iscomplete: false,
    islate: false,
    priority: '',
    remark: ''
  });

  // 列宽调整功能
  useEffect(() => {
    if (!tableRef.current) return;

    const table = tableRef.current;
    const cols = table.querySelectorAll('th');
    let isResizing = false;
    let currentCol = null;
    let startX = 0;
    let startWidth = 0;

    const handleMouseDown = (e) => {
      if (e.target.classList.contains('resizer')) {
        isResizing = true;
        currentCol = e.target.parentElement;
        startX = e.pageX;
        startWidth = currentCol.offsetWidth;
        document.body.style.cursor = 'col-resize';
      }
    };

    const handleMouseMove = (e) => {
      if (!isResizing) return;
      const width = startWidth + (e.pageX - startX);
      if (width > 50) {
        currentCol.style.width = `${width}px`;
      }
    };

    const handleMouseUp = () => {
      isResizing = false;
      document.body.style.cursor = '';
    };

    table.addEventListener('mousedown', handleMouseDown);
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      table.removeEventListener('mousedown', handleMouseDown);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, []);

  // 处理添加新行
  const handleAddRow = () => {
    setEditingRow({...newRowData, id: Date.now()});
  };

  // 处理保存编辑的行
  const handleSaveRow = () => {
    if (editingRow) {
      setData([...data, editingRow]);
      setEditingRow(null);
      setNewRowData({
        task_name: '',
        task_step: '',
        startdate: '',
        enddate: '',
        responsibility: '',
        taskstate: '',
        iscomplete: false,
        islate: false,
        priority: '',
        remark: ''
      });
    }
  };

  // 处理取消编辑
  const handleCancelEdit = () => {
    setEditingRow(null);
  };

  // 处理输入变化
  const handleInputChange = (e, field) => {
    if (editingRow) {
      setEditingRow({
        ...editingRow,
        [field]: e.target.value
      });
    } else {
      setNewRowData({
        ...newRowData,
        [field]: e.target.value
      });
    }
  };

  return (
    <div className="table-container">
      <table ref={tableRef} className="resizable-table">
        <thead>
          <tr>
            <th>任务名称 <div className="resizer"></div></th>
            <th>任务步骤 <div className="resizer"></div></th>
            <th>开始日期 <div className="resizer"></div></th>
            <th>截至日期 <div className="resizer"></div></th>
            <th>责任人 <div className="resizer"></div></th>
            <th>当前状态 <div className="resizer"></div></th>
            <th>是否完成 <div className="resizer"></div></th>
            <th>是否超期 <div className="resizer"></div></th>
            <th>优先级 <div className="resizer"></div></th>
            <th>备注 <div className="resizer"></div></th>
          </tr>
        </thead>
        <tbody>
          {/* 编辑行 */}
          {editingRow && (
            <tr className="editing-row">
              <td><input type="text" value={editingRow.task_name} onChange={(e) => handleInputChange(e, 'task_name')} /></td>
              <td><input type="text" value={editingRow.task_step} onChange={(e) => handleInputChange(e, 'task_step')} /></td>
              <td><input type="text" value={editingRow.startdate} onChange={(e) => handleInputChange(e, 'startdate')} /></td>
              <td><input type="text" value={editingRow.enddate} onChange={(e) => handleInputChange(e, 'enddate')} /></td>
              <td><input type="text" value={editingRow.responsibility} onChange={(e) => handleInputChange(e, 'responsibility')} /></td>
              <td><input type="text" value={editingRow.taskstate} onChange={(e) => handleInputChange(e, 'taskstate')} /></td>
              <td><input type="checkbox" checked={editingRow.iscomplete} onChange={(e) => handleInputChange({target: {value: e.target.checked}}, 'iscomplete')} /></td>
              <td><input type="checkbox" checked={editingRow.islate} onChange={(e) => handleInputChange({target: {value: e.target.checked}}, 'islate')} /></td>
              <td><input type="text" value={editingRow.priority} onChange={(e) => handleInputChange(e, 'priority')} /></td>
              <td><input type="text" value={editingRow.remark} onChange={(e) => handleInputChange(e, 'remark')} /></td>
            </tr>
          )}

          {/* 数据行 */}
          {data.map(row => (
            <tr key={row.id}>
              <td>{row.task_name}</td>
              <td>{row.task_step}</td>
              <td>{row.startdate}</td>
              <td>{row.enddate}</td>
              <td>{row.responsibility}</td>
              <td>{row.taskstate}</td>
              <td>{row.iscomplete ? '是' : '否'}</td>
              <td>{row.islate ? '是' : '否'}</td>
              <td>{row.priority}</td>
              <td>{row.remark}</td>
            </tr>
          ))}

          {/* 添加行按钮 */}
          <tr className="add-row">
            <td colSpan="1">
              <button className="add-button" onClick={handleAddRow}>+
                <span className="tooltip-text">添加新行</span>
              </button>
            </td>
            <td colSpan="9"></td>
          </tr>
        </tbody>
      </table>

      {/* 保存和取消按钮 */}
      {editingRow && (
        <div className="edit-buttons">
          <button className="save-button" onClick={handleSaveRow}>保存</button>
          <button className="cancel-button" onClick={handleCancelEdit}>取消</button>
        </div>
      )}
    </div>
  );
};

export default TableComponent;
