import React, { useState } from 'react';
import { Container, Typography, Box } from '@mui/material';
import StatisticsDashboard from './components/StatisticsDashboard.js';
import TaskTable from './components/TaskTable';
import StepTable from './components/StepTable';

function App() {
  const [refreshStats, setRefreshStats] = useState(false);

  // 触发统计数据刷新
  const triggerStatsRefresh = () => {
    setRefreshStats(!refreshStats);
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          项目管理仪表盘
        </Typography>

        {/* 统计图表区域 - 放置在header下面，表格上方 */}
        <StatisticsDashboard key={refreshStats} />

        {/* 任务表格 */}
        <TaskTable onDataChange={triggerStatsRefresh} />

        {/* 步骤表格 */}
        <StepTable onDataChange={triggerStatsRefresh} />
      </Box>
    </Container>
  );
}

export default App;
